#判断路径所包含的路段
N=8
m = Model("master")#定义主问题
#主问题变量
Setup= m.addVars(Segs,vtype=GRB.BINARY,name="setup")#road-ban决策
Z=m.addVars(Transportation,Route,vtype=GRB.BINARY,name="Z")#路径通行变量
Choice=m.addVars(Transportation,Route,vtype=GRB.BINARY,name="C")

m.setObjective(sum(Demand[t]*Possibility[i]*Judge[t][k][i]*Consequence[i]*Choice[t,k] for t in Transportation for k in Route for i in Segs),GRB.MINIMIZE)#目标函数
m.addConstrs((Z.sum(t,'*')>=1 for t in Transportation),"TL")#如果有运输，必须至少保留一条通路
m.addConstrs((Z[t,k]>=sum(Judge[t][k][i]*Setup[i] for i in Segs)-sum(Judge[t][k][i] for i in Segs) + 1 for t in Transportation for k in Route),"link")#只有路径包含的所有路段开放，该路径才可通行
m.addConstrs((Z[t,k]<=Setup[i]-Judge[t][k][i]+1 for t in Transportation for k in Route for i in Segs),"LINK")#任何一个路段封闭，所在的路径则关闭
m.addConstr(sum(1-Setup[i] for i in Segs)<=N,"number")#规定关闭的弧数量
m.addConstrs((Choice[t,k]<=Z[t,k] for t in Transportation for k in Route),"a")
m.addConstrs((Choice[t,k]>=Z[t,k]-sum(Z[t,l] for l in range(k)) for t in Transportation for k in Route),"b")
m.Params.LogToConsole=0
m.optimize()  
print('\nTOTAL COSTS: %g' % m.objVal)       
for t in Transportation:
    for k in Route:
        if Choice[t,k].x>=0.5:
            print('Choice[%d,%d]=%g' % (t,k,Choice[t,k].x)) 
for i in Segs:
    if Setup[i].x<=0.5:
        print('X[%d]=%g' % (i,Setup[i].x))

    
    
    
    